window.addEventListener('click', (e) => {
	if(e.target !== document.querySelector('#confirm') && e.target !== document.querySelector('#cancel')) return
	const dialog = document.getElementById('dialog-with-slot')
	dialog.open = false;
}, { passive: true, capture: true })

window.addEventListener("DOMContentLoaded", () => {addLog()})
window.addEventListener("reset", () => {addLog()})
function addLog(){
const log = document.querySelector('rux-log')
if(!log) return
const data = [
	{
		timestamp: new Date(),
			status: 'off',
			message: 'Antenna DGS 1 went offline.',
		},{
		timestamp: new Date(),
			status: 'serious',
			message: 'USA-177 experienced solar panel misalignment.',
		},{
		timestamp: new Date(),
			status: 'caution',
			message: 'USA-168 suffered power degradation.',
		},{
		timestamp: new Date(),
			status: 'standby',
			message: 'Antenna DGS 2 has weak signal.',
		},{
		timestamp: new Date(),
			status: 'off',
			message: 'Antenna DGS 1 went offline.',
		},{
		timestamp: new Date(),
			status: 'serious',
			message: 'USA-177 experienced solar panel misalignment.',
		},{
		timestamp: new Date(),
			status: 'caution',
			message: 'USA-168 suffered power degradation.',
		},{
		timestamp: new Date(),
			status: 'standby',
			message: 'Antenna DGS 2 has weak signal.',
		}
	]
log.data = data}

window.addEventListener("DOMContentLoaded", () => {setData()})
window.addEventListener("reset", () => {setData()})
function setData(){
const segmented = document.querySelector('rux-segmented-button')
 if(!segmented) return
const data = [
{ label: 'First item' },
{ label: 'Second item'},
{ label: 'Third item' },
]
segmented.data = data};

let timelineExpanded = false;
window.addEventListener('click', (e) => {
 if(e.target === document.querySelector('.click-trigger') || e.target === document.querySelector('#triggerIcon')) {
const additionalTracks = document.querySelectorAll(".expanded");
const mainTrack = document.querySelector("#headerTrack");
const mainTrackEvents = mainTrack.querySelectorAll("rux-time-region");
const icon = document.querySelector("rux-icon");
const toggleTrack = () => {
	timelineExpanded = !timelineExpanded;
	//set icon style
	icon.style.transform = timelineExpanded ? "rotate(90deg)" : "rotate(0)";
	//toggle the expanded and collapsed sections
	for (trackEvent of mainTrackEvents) {
	trackEvent.style.display = timelineExpanded ? "none" : "block";
	}
	for (track of additionalTracks) {
	track.style.display = timelineExpanded ? "block" : "none";
	}
}
toggleTrack()
}
});
